import axios from "axios"

const App = () => {
  const [infor, setInfor] = useState([])
  const [country, setCountry] = useState('')

  useEffect(() => {
      console.log(country)
      axios
        .get(`https://restcountries.com/v3.1/all`)
        .then(response => {
          setInfor(response.data)
        })
  }, [])
  console.log({infor})
  const countryToShow = country.length === 0 ? [] : infor.filter(countrie => countrie.name.common.toLowerCase().includes(country.toLowerCase()))
  const handleChange = (event) => {
    setCountry(event.target.value)
  }
  return (
    <div>
      <form>
