  useEffect(() => {
    if(country) {
      console.log(country)
      axios
        .get(`https://restcountries.com/v3.1/name/${country}`)
        .then(response => {
          //console.log(response.data.name.common)
          const country = response.date
          setInfor(response.data)
        })
    }
